const RealTimeReport = () => {
  let [img_num, setImg_num] = useState(0)
let [displayBlock , setDisplayblock] = useState(true)
let [displayNone , setDisplayNone] = useState({"display":"none"})
  let g = (k) => {
    setImg_num(k)
  }

  let changeOption = () =>{
    setDisplayblock(!displayBlock)
  }
  useEffect(() => {
    const interval = setInterval(() => {
      setImg_num((prevIndex) => {
        if (prevIndex > leftOptions.length) {
          prevIndex = 0
          return (prevIndex)
        }
        else {
          return (prevIndex + 1)
        }
      }
      )
    }, 2000);

    return () => {
      clearInterval(interval);
    };
  })
  return (
    <React.Fragment>
      <div>RealTimeReport</div>
    <div className="RealTimeReport flex">
      <div className="report_Options flex">
        <div className="options left_options" onClick={() =>changeOption()}>
          {
            leftOptions.map((item, index) => {
              return (<React.Fragment>
                <div className="optionInfo flex flex-row justify-center pr-4 pl-7 mb-7 items-center" key={item.key}>
                  <div className="optionSvg relative w-[2.08vw] h-[2.08vw] bg-[#ECF6FF] border-[2px] border-f_primary  rounded-full font-bold">{item.svg}</div>
                  <div id={index} className="optionName" onClick={() => g(index)}>{item.optionName}</div>
                </div>
              </React.Fragment>)
            })
          }


        </div>
        <div className="options right_options" onClick={() =>changeOption()}>
        
          {
            RightOptions.map((item, index) => {
              return (<React.Fragment>
                <div className="optionInfo flex flex-row justify-center pr-4 pl-7 mb-7 items-center" key={item.key}>
                  <div className="optionSvg relative w-[2.08vw] h-[2.08vw] bg-[#ECF6FF] border-[2px] border-f_primary  rounded-full font-bold">{item.svg}</div>
                  <div id={index} className="optionName" onClick={() => g(index)}>{item.optionName}</div>
                </div>
              </React.Fragment>)
            })
          }


        
        </div>
      </div>
      <div className="report_Images" style={{"display" : displayBlock ? "block" : "none"}}>
      {leftOptions.map((i, index) => {
        return (
          <div className="Images left_images" >
            <div className="shadowPart flex">
              <div className="shadowDetails">
                <div className="optionName" >{i.optionNames}</div>
                <div className="optionNums">{i.optionNums}</div>
                <div className="optionUnit">{i.optionUnit}</div>
              </div>
              <div className="shadowImg">
                <img src={i.imgSrc_bg} alt="shadowImg" />
              </div>
            </div>

            {
              index === img_num &&
              <div className="realImg" id={index}>
                <img src={leftOptions[img_num].imgSrc} alt="realImg" />
              </div>}
         
         
          </div>)
      })
    }
      </div>
      <div className="report_Images" style={{"display": displayBlock ?  "none" : "block"}}>
      {RightOptions.map((i, index) => {
        return (
          <div className="Images left_images" >
            <div className="shadowPart flex">
              <div className="shadowDetails">
                <div className="optionName" >{i.optionNames}</div>
                <div className="optionNums">{i.optionNums}</div>
                <div className="optionUnit">{i.optionUnit}</div>
              </div>
              <div className="shadowImg">
                <img src={i.imgSrc_bg} alt="shadowImg" />
              </div>
            </div>

            {
              index === img_num &&
              <div className="realImg" id={index}>
                <img src={RightOptions[img_num].imgSrc} alt="realImg" />
              </div>}
         
         
          </div>)
      })
    }
      </div>
    </div>
    </React.Fragment>
  );
};

export default RealTimeReport;
